const { getDocumentsFolder } = require("platform-folders");
const path = require("path");
const fs = require("fs");

function logsData(id) {
    const documentsPath = getDocumentsFolder();
    let gameLogFile;

    if (!id) return false;
    else if (id == 1) {
        gameLogFile = path.join(documentsPath, "Euro Truck Simulator 2", "game.log.txt")
    } else if (id == 2) {
        gameLogFile = path.join(documentsPath, "American Truck Simulator", "game.log.txt")
    }

    const data = fs.readFileSync(gameLogFile, "utf8");
    const split1 = data.split("TruckersMP/")[1];
    // const split2 = split1.split(".exe")[0];

    return !!split1;
}

module.exports = logsData